// src/components/MenuCard.js

import React from 'react';

const MenuCard = ({ menu, onMenuClick }) => {
  return (
    <div className="menu-card">
      <img 
        src={menu.image} 
        alt={menu.nom} 
        className="menu-card-image"
        onClick={() => onMenuClick(menu)}
      />
      <div className="menu-card-content">
        <h3 className="menu-card-title">{menu.nom}</h3>
        <p className="menu-card-description">{menu.description}</p>
        <p className="menu-card-price">{menu.prix.toFixed(2)} â‚¬</p>
      </div>
    </div>
  );
};

export default MenuCard;
